import { useState, useEffect } from "react";
import { Button, Card, Row, Col } from "react-bootstrap";
import "../getAPI.js";
import getPokemon from "../getAPI.js";

function Pokemon1() {
	const [src, setSrc] = useState();
	const [name, setName] = useState();
	const [infor, setInfor] = useState();

	useEffect(() => {
		getPokemon(1).then((data) => {
			setSrc(data.sprites["front_default"]);
			setInfor(data);
			console.log(data);
		});
	}, []);
	function getInfor(type) {
		if (infor !== undefined) {
			console.log(infor["stats[1].base_stat"]);
			// if(type === "name"){
			// 	return infor.name;
			// } else if()
			// return infor[type];
			switch (type) {
				case "name":
					return infor.name;
				case "hp":
					return infor.stats[0].base_stat;
				case "attack":
					return infor.stats[1].base_stat;
				default:
					return "";
			}
		} else {
			console.log("wait");
		}
	}
	return (
		<>
			<Card style={{ width: "15rem", margin: "auto" }}>
				<Card.Img variant="top" src={src} />
				<Card.Body>
					<Card.Title style={{ textAlign: "center" }}>
						{infor !== undefined ? infor.name : ""}
					</Card.Title>
					<Row>
						<Col>
							<Card.Text>Attack: {getInfor("attack")}</Card.Text>
						</Col>
						<Col>
							<Card.Text>Defence</Card.Text>
						</Col>
					</Row>
					<Card.Text>
						Some quick example text to build on the card title and make up the
						bulk of the card's content.
					</Card.Text>
					<Button variant="primary">Go somewhere</Button>
				</Card.Body>
			</Card>
		</>
	);
}
export default Pokemon1;
