import $ from "jquery";
const getPokemon = async function (pokemon) {
	const res = await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon}/`).then(
		(response) =>
			response.json().then((user) => {
				return user.getPokemon;
			})
	);
	const data = await res.json();
	console.log(data);
	return data;
	// $("#root").append(`<img src=${data.sprites["front_default"]} />
	// <div>HP: ${data.stats[0].base_stat}| Atack: ${data.stats[1].base_stat}| Defense ${data.stats[2].base_stat}</div>`);
};
export default getPokemon;
